mapper.insert:
{CALL PROC_INSERT_PAYMENT(
		#{cartId, mode=IN, jdbcType=NUMERIC},
		#{amount, mode=IN, jdbcType=NUMERIC},
		#{paymentCard, mode=IN, jdbcType=VARCHAR},
		#{paymentKey, mode=IN, jdbcType=VARCHAR},
		#{paymentTime, mode=IN, jdbcType=TIMESTAMP},
		#{paymentId, mode=OUT, jdbcType=NUMERIC}
	)}

DECLARE v_name VARCHAR2(100); v_price NUMBER; v_thumbnail_image VARCHAR2(255); BEGIN
-- 각 상품에 대한 정보를 변수에 저장 SELECT P.NAME, P.PRICE, P.THUMBNAIL_IMAGE INTO v_name, v_price, v_thumbnail_image FROM PRODUCT P WHERE P.PRODUCT_ID = #{item.productId};
-- 결제 아이템 삽입 INSERT INTO PAYMENT_ITEM ( PAYMENT_ITEM_ID, PAYMENT_ID, PRODUCT_ID, NAME, PRICE, QUANTITY, THUMBNAIL_IMAGE ) VALUES ( SEQ_PAYMENT_ITEM_ID.NEXTVAL, #{paymentId}, #{item.productId}, v_name, v_price, #{item.quantity}, v_thumbnail_image );
END;

CALL PROC_INSERT_CART_AND_QRCODE(
	#{cartId, mode=IN, jdbcType=NUMERIC},
	#{paymentId, mode=IN, jdbcType=NUMERIC},
	#{qrCodeUri, mode=IN, jdbcType=VARCHAR},
	#{newCartId, mode=OUT, jdbcType=NUMERIC, javaType=java.lang.Long}
	)
